import React, { useState, useEffect } from 'react'; import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';

function PaperComponent(props) {
   return (
      <Draggable cancel={'[class*="MuiDialogContent-root"]'}>
         <Paper {...props} />
      </Draggable>
   );
}

const useStyles = makeStyles(theme => ({
   formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
   },
   container: {
      display: 'flex',
      justifyContent: 'center',
      flexWrap: 'wrap',
   },
   input: {
      margin: theme.spacing(1),
   },
}));

const AddBarang = ({ openAdd, modalAdd, addFunc }) => {
   const classes = useStyles();

   useEffect(() => {
      effect
      return () => {
         cleanup
      };
   }, [input])

   const [po, setPo] = useState({
      nama_plg: '',
      nama_brg: '',
      jumlah: '',
      tgl_krm: Date.now()
   })

   const handleFieldChange = e => setPo({ ...po, [e.target.name]: e.target.value })

   const { nama_plg, nama_brg, jumlah, tgl_krm } = po

   const handleAdd = (e) => {
      e.preventDefault()
      addFunc(po)
      setPo({
         nama_plg: '',
         nama_brg: '',
         jumlah: '',
         tgl_krm: ''
      })
   }

   return (
      <Dialog
         open={openAdd}
         onClose={modalAdd}
         PaperComponent={PaperComponent}
         aria-labelledby="draggable-dialog-title"
      >
         <DialogTitle style={{ cursor: 'move' }} id="draggable-dialog-title">
            Tambah PO
         </DialogTitle>
         <form onSubmit={handleAdd}>
            <DialogContent>
               <div className={classes.container}>
                  <TextField
                     id="date"
                     name="tgl_krm"
                     label="Tanggal Kirim"
                     type="date"
                     value={tgl_krm}
                     onChange={handleFieldChange}
                     className={classes.input}
                     InputLabelProps={{
                        shrink: true,
                     }}
                  />
                  <FormControl className={classes.formControl}>
                     <InputLabel id="Nama Barang">Nama Barang</InputLabel>
                     <Select
                        labelId="Nama Barang"
                        value={""}
                        onChange={""}
                     >
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                     </Select>
                  </FormControl>
                  <Input
                     placeholder="Nama Pelanggan"
                     className={classes.input}
                     inputProps={{
                        'aria-label': "Nama Pelanggan",
                     }}
                     name="nama_plg"
                     value={nama_plg}
                     onChange={handleFieldChange}
                     required
                  />
                  {/* <Input
                     placeholder="Nama Barang"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Nama Barang',
                     }}
                     name="nama_brg"
                     value={nama_brg}
                     onChange={handleFieldChange}
                     required
                  /> */}
                  <Input
                     placeholder="Jumlah"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'jumlah',
                     }}
                     name="jumlah"
                     value={jumlah}
                     onChange={handleFieldChange}
                     required
                  />
                  {/* <Input
                     placeholder="Status"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Status',
                     }}
                     name="status"
                     value={status}
                     onChange={handleFieldChange}
                     required
                  /> */}
               </div>
            </DialogContent>
            <DialogActions>
               <Button autoFocus onClick={modalAdd} color="primary">
                  Cancel
               </Button>
               <Button type="submit" color="primary">
                  Tambah
               </Button>
            </DialogActions>
         </form>
      </Dialog>
   )
}

export default AddBarang
