import React, { forwardRef, createRef, useState, useEffect } from 'react';
import axios from 'axios';
import MaterialTable from 'material-table';
import moment from 'moment';

import Grid from '@material-ui/core/Grid';
import Chip from '@material-ui/core/Chip';
import AddBox from '@material-ui/icons/AddBox';
import ArrowUpward from '@material-ui/icons/ArrowUpward';
import Check from '@material-ui/icons/Check';
import ChevronLeft from '@material-ui/icons/ChevronLeft';
import ChevronRight from '@material-ui/icons/ChevronRight';
import Clear from '@material-ui/icons/Clear';
import FilterList from '@material-ui/icons/FilterList';
import FirstPage from '@material-ui/icons/FirstPage';
import LastPage from '@material-ui/icons/LastPage';
import Refresh from '@material-ui/icons/Refresh';
import Remove from '@material-ui/icons/Remove';
import SaveAlt from '@material-ui/icons/SaveAlt';
import SaveIcon from '@material-ui/icons/Save';
import Search from '@material-ui/icons/Search';
import ViewColumn from '@material-ui/icons/ViewColumn';

import DashboardLayout from 'layouts/Dashboard';
import { withStyles } from '@material-ui/core/styles';

import SnackbarComponent from '../../components/Snackbar/SnackbarComponent';

const tableIcons = {
  Add: forwardRef((props, ref) => <AddBox
    {...props}
    ref={ref}
                                  />),
  Check: forwardRef((props, ref) => <SaveIcon
    {...props}
    ref={ref}
                                    />),
  Clear: forwardRef((props, ref) => <Clear
    {...props}
    ref={ref}
                                    />),
  Delete: forwardRef((props, ref) => (
    <Clear
      {...props}
      color="secondary"
      ref={ref}
    />
  )),
  DetailPanel: forwardRef((props, ref) => (
    <ChevronRight
      {...props}
      ref={ref}
    />
  )),
  Edit: forwardRef((props, ref) => (
    <Check
      {...props}
      color="primary"
      ref={ref}
    />
  )),
  Export: forwardRef((props, ref) => <SaveAlt
    {...props}
    ref={ref}
                                     />),
  Filter: forwardRef((props, ref) => <FilterList
    {...props}
    ref={ref}
                                     />),
  FirstPage: forwardRef((props, ref) => <FirstPage
    {...props}
    ref={ref}
                                        />),
  LastPage: forwardRef((props, ref) => <LastPage
    {...props}
    ref={ref}
                                       />),
  NextPage: forwardRef((props, ref) => <ChevronRight
    {...props}
    ref={ref}
                                       />),
  PreviousPage: forwardRef((props, ref) => (
    <ChevronLeft
      {...props}
      ref={ref}
    />
  )),
  Refresh: forwardRef((props, ref) => <Refresh
    {...props}
    ref={ref}
                                      />),
  ResetSearch: forwardRef((props, ref) => <Clear
    {...props}
    ref={ref}
                                          />),
  Search: forwardRef((props, ref) => <Search
    {...props}
    ref={ref}
                                     />),
  SortArrow: forwardRef((props, ref) => <ArrowUpward
    {...props}
    ref={ref}
                                        />),
  ThirdStateCheck: forwardRef((props, ref) => <Remove
    {...props}
    ref={ref}
                                              />),
  ViewColumn: forwardRef((props, ref) => <ViewColumn
    {...props}
    ref={ref}
                                         />)
};

const styles = theme => ({
  root: {
    padding: theme.spacing.unit * 5
  },
  item: {
    height: '100%'
  }
});

const SuratJalan = props => {
  const tableRef = createRef();
  const { classes } = props;

  const [state] = React.useState({
    columns: [
      { title: 'No PO', field: 'no_po', editable: 'never' },
      {
        title: 'Tanggal PO',
        field: 'tgl_po',
        render: row => moment(Date.now(row.tgl_po)).format('DD-MM-YYYY'),
        editable: 'never'
      },
      { title: 'Nama pelanggan', field: 'nama_plg', editable: 'never' },
      { title: 'Nama Barang', field: 'nama_brg', editable: 'never' },
      { title: 'Jumlah', field: 'jumlah', editable: 'never' },
      {
        title: 'Tanggal Kirim',
        field: 'tgl_krm',
        type: 'date',
        render: row => moment(row.tgl_krm).format('DD-MM-YYYY'),
        editable: 'never'
      }
    ]
  });

  const [snackbarAdd, setSnackbarAdd] = useState({
    open: false,
    message: '',
    variant: ''
  });
  const handleCloseSnackbarAdd = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSnackbarAdd({
      open: false,
      message: '',
      variant: ''
    });
  };

  const getData = () => {
    return query =>
      new Promise((resolve, reject) => {
        fetch(
          'http://localhost:8001/api/sj' +
            `?limit=${query.pageSize}&page=${query.page + 1}&search=${
              query.search
            }`
        )
          .then(res => res.json())
          .then(result => {
            resolve({
              data: result[0].data, // your data array
              page: result[0].page - 1, // current page number
              totalCount: result[0].total // total value
            });
          });
      });
  };

  return (
    <DashboardLayout title="Data PO">
      <div className={classes.root}>
        {snackbarAdd.open && (
          <SnackbarComponent
            handleClose={handleCloseSnackbarAdd}
            message={snackbarAdd.message}
            open={snackbarAdd.open}
            variant={snackbarAdd.variant}
          />
        )}
        <MaterialTable
          columns={state.columns}
          data={getData()}
          detailPanel={rowData => {
            return (
               <diV style={{padding:'20px 50px', ba}}>
                  <Grid container spacing={3}>
                     <Grid item xs={3}>
                        <img alt="Company logo" className={classes.logoImage} src="/images/logos/pancamitra.png"/>
                        {/* <p>Jl. Palem Manis 3 No 65 Jatiuwung Tangerang Banten,<br/> RT.004/RW.003, Gandasari, Jatiuwung, Tangerang City,<br/> Banten 15136</p> */}
                     </Grid>
                     <Grid item xs={6}>
                        <h2 style={{marginTop:"30px", marginLeft: '150px'}}>SURAT JALAN</h2>
                     </Grid>
                     <Grid item xs={3}>
                        <p style={{marginTop:"10px"}}>Jl. Palem Manis 3 No 65 Jatiuwung Tangerang Banten, RT.004/RW.003, Gandasari, Jatiuwung, Tangerang City, Banten 15136</p>
                     </Grid>
                  </Grid>
               </diV>

            );
          }}
          icons={tableIcons}
          onRowClick={(event, rowData, togglePanel) => togglePanel()}
          options={{
            actionsColumnIndex: -1,
            exportButton: true,
            paginationType: 'stepped',
            pageSize: 10,
            pageSizeOptions: [10, 25, 50],
            debounceInterval: 0,
            exportAllData: true
          }}
          tableRef={tableRef}
          title="Data Pre Order"
        />
      </div>
    </DashboardLayout>
  );
};

export default withStyles(styles)(SuratJalan);
