import React, { Component } from 'react';

// Externals
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { Bar } from 'react-chartjs-2';

// Material helpers
import { withStyles } from '@material-ui/core/styles';

// Material components
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowRightIcon from '@material-ui/icons/ArrowRight';

// Shared components
import Portlet from 'components/Portlet';
import PortletContent from 'components/PortletContent';
import PortletFooter from 'components/PortletFooter';

// Palette
import palette from 'theme/palette';

// Chart configuration
import { data, options } from './chart';

// Component styles
import styles from './styles';

class TabsChart extends Component {
  render() {
    const { classes, className, ...rest } = this.props;

    const rootClassName = classNames(classes.root, className);

    return (
      <Portlet
        {...rest}
        className={rootClassName}
      >
        <PortletContent noPadding>
          <div className={classes.details}>
            <div className={classes.summary}>
              <Typography variant="h1">20</Typography>
              <Typography variant="body1">total PO</Typography>
            </div>
            <div className={classes.legends}>
              <div className={classes.legend}>
                <span
                  className={classes.legendColor}
                  style={{ backgroundColor: palette.primary.main }}
                />
                <Typography varint="body1">Success</Typography>
              </div>
              <div className={classes.legend}>
                <span
                  className={classes.legendColor}
                  style={{ backgroundColor: '#f48fb1l' }}
                />
                <Typography varint="body1">Pending</Typography>
              </div>
            </div>
          </div>
          <div className={classes.chart}>
            <Bar
              data={data}
              options={options}
            />
          </div>
        </PortletContent>
        <PortletFooter className={classes.portletFooter}>
          <Button variant="text">
            Last 7 days <ArrowDropDownIcon />
          </Button>
          <Button
            color="primary"
            variant="text"
          >
            Audience Overview <ArrowRightIcon />
          </Button>
        </PortletFooter>
      </Portlet>
    );
  }
}

TabsChart.propTypes = {
  className: PropTypes.string,
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(TabsChart);
