import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import Paper from '@material-ui/core/Paper';
import Draggable from 'react-draggable';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';

function PaperComponent(props) {
   return (
      <Draggable cancel={'[class*="MuiDialogContent-root"]'}>
         <Paper {...props} />
      </Draggable>
   );
}

const useStyles = makeStyles(theme => ({
   container: {
      display: 'flex',
      flexWrap: 'wrap',
   },
   input: {
      margin: theme.spacing(1),
   },
}));

const AddBarang = ({ openAdd, modalAdd, addFunc }) => {
   const classes = useStyles();

   const [po, setPo] = useState({
      tgl_po: '',
      nama_plg: '',
      nama_brg: '',
      jumlah: '',
      tgl_krm: '',
      status: ''
   })

   const handleFieldChange = e => setBarang({ ...barang, [e.target.name]: e.target.value })

   const {tgl_po, nama_plg, nama_brg, jumlah, tgl_krm, status} = po

   const handleAdd = (e) => {
      e.preventDefault()
      addFunc(po)
      setPo({
         tgl_po: '',
         nama_plg: '',
         nama_brg: '',
         jumlah: '',
         tgl_krm: '',
         status: ''
      })
   }

   return (
      <Dialog
         open={openAdd}
         onClose={modalAdd}
         PaperComponent={PaperComponent}
         aria-labelledby="draggable-dialog-title"
      >
         <DialogTitle style={{ cursor: 'move' }} id="draggable-dialog-title">
            Tambah PO
         </DialogTitle>
         <form onSubmit={handleAdd}>
            <DialogContent>
               <div className={classes.container}>
                  <Input
                     placeholder="Tanggal PO"
                     className={classes.input}
                     inputProps={{
                        'aria-label': "Tanggal PO",
                     }}
                     name="tgl_po"
                     value={tgl_po}
                     onChange={handleFieldChange}
                     required
                  />
                  <Input
                     placeholder="Nama Pelanggan"
                     className={classes.input}
                     inputProps={{
                        'aria-label': "Nama Pelanggan",
                     }}
                     name="nama_barang"
                     value={nama_barang}
                     onChange={handleFieldChange}
                     required
                  />
                  <Input
                     placeholder="Tipe"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Tipe',
                     }}
                     name="tipe"
                     value={tipe}
                     onChange={handleFieldChange}
                     required
                  />
                  <Input
                     placeholder="Accept"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Accept',
                     }}
                     name="accept"
                     value={accept}
                     onChange={handleFieldChange}
                     required
                  />
                  <Input
                     placeholder="Rejects"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Rejects',
                     }}
                     name="rejects"
                     value={rejects}
                     onChange={handleFieldChange}
                     required
                  />
                  <Input
                     placeholder="Harga"
                     className={classes.input}
                     inputProps={{
                        'aria-label': 'Harga',
                     }}
                     name="harga"
                     value={harga}
                     onChange={handleFieldChange}
                     required
                  />
               </div>
            </DialogContent>
            <DialogActions>
               <Button autoFocus onClick={modalAdd} color="primary">
                  Cancel
               </Button>
               <Button type="submit" color="primary">
                  Tambah
               </Button>
            </DialogActions>
         </form>
      </Dialog>
   )
}

export default AddBarang
